import React, { Component } from "react";
import "./App.css";
import Nav from "./components/Nav";
import About from "./components/About";
import UserForm from "./components/UserForm"
import Activity from "./components/Activity";
import Profile from "./components/Profile"
import List from "./components/List"
import { getUser } from "./services/auth"
const API_URL = 'http://localhost:3000/';

class App extends Component {
  constructor() {
    super();
    this.state = {

      navs: ["Home", "About", "Activity"],
      activeNav: "home",
      activePage: "home",
      clickedActivity: null  , 
      user: null,
      userInfo: [],
      activityInfo: [],
      listInfo: [],
      username: '',
      activity: [],
      list: [],

      activityForm: false,
      listForm: false,
      loginForm: false,
      userForm: false,
      showProfile: false,
    };
  }

  checkForUser() {
    const user = getUser();
    if (user) {
      this.setState({ user });
    }
  }

  // fetch all activity
  componentDidMount() {

    this.checkForUser();

    if (this.state.user) {
      this.setState({ navs: this.state.navs.concat(["sigin out"]) })
    } else {
      this.setState({ navs: this.state.navs.concat(["login"]) })
    }

    console.log(this.state.navs)
    fetch(`${API_URL}activity`)
      .then((res) => { return res.json() })
      .then((data) => {
        console.log("get all activity data", data)
        this.setState({ activity: data });
      })
  }

  /*************** NAV *********************/
  onNavClick = activeNav => {
    console.log("activeNav toggle")
    this.setState({ activeNav });
  };

  // show the login form 
  setLoginForm() {
    this.setState({
      loginForm: !this.state.loginForm,
      userForm: false,
    });
  }

  handleChange(event) {
    this.setState({
      username: event.target.value
    })
  }

  // handle the user name to check if he is registered or no 
  handleSubmit(event) {
    event.preventDefault();

    fetch(`${API_URL}user/${this.state.username}`)
      .then(response => response.json())
      .then(data => {
        this.setState({
          userInfo: data,
          loginForm: false,
          activityForm: true
        })
      })
      .catch(error => {
        console.log('error in submit user: ', error);
        alert("This username NOT Registered");
      })
    console.log("this.state.userInfo:", this.state.userInfo);
  }
  //handel thre form sumbmission if the user to create a new user or updtae user info
  handleFormSubmit(user) {
    this.state.userInfo ? this.updateUserInfo(user) : this.createNewUser(user)
  }

  // update user information in the database 
  updateUserInfo(user) {
    console.log("update user info ", user);
    const url = `${API_URL}user/${user.id}`

    fetch(url, {
      method: 'PUT',
      headers: {
        "Content-Type": "application/json"
      },
      body: JSON.stringify(user)
    })
      .then(response => response.json())
      .then(data => {
        this.setState({
          userInfo: data,
          userForm: false
        })
      })
      .catch(error => {
        console.log(error);
      })
  }



  // create user information in the database 
  createNewUser(user) {
    const url = `${API_URL}user`
    console.log("create user info", user)
    fetch(url, {
      method: 'POST',
      headers: {
        "Content-Type": "application/json"
      },
      body: JSON.stringify(user)
    })
      .then(response => response.json())
      .then(data => {
        console.log("create user info  data", data)

        this.setState({
          userInfo: data,
          userForm: false
        })
      })
      .catch(error => {
        console.log('create New User Error: ', error)
        alert("Tnis username or email is Registerd.");
      })
  }


  //handel new user 
  handleRegister() {
    this.setState({
      userForm: !this.state.userForm,
      loginForm: false
    });
  }


  // render user form for the registeration
  renderUserForm() {
    return <UserForm userInfo={this.state.userInfo}
      handleFormSubmit={this.handleFormSubmit.bind(this)}
      handleRegister={this.handleRegister.bind(this)}
    />
  }

  // render the user profile component
  renderUserProfile() {
    return <Profile user={this.state.userInfo}
      handleRegister={this.handleRegister.bind(this)}
      setUserProfile={this.setUserProfile.bind(this)}
    />
  }

  // claer the state for log out
  handleLogout() {
    this.setState({
      username: '',
      userInfo: undefined,
      showProfile: false,
      userForm: false
    })
  }

  //This function will render the log-in form it the login is true
  renderLoginForm() {
    return (
      <div className="login p-5">
        <form onSubmit={this.handleSubmit.bind(this)}>
          <div className="close-modal" onClick={() => this.setLoginForm()}>x</div>

          <div class="row">
            <div class="col-sm">
              <label class="sr-only" for="inlineFormInputGroup">Username</label>
              <div class="input-group mb-2">
                <div class="input-group-prepend">
                  <div class="input-group-text">@</div>
                </div>
                <input type="text" className="form-control" id="inlineFormInputGroupUsername" placeholder="Username" onChange={this.handleChange.bind(this)} />
              </div>
            </div>


            <div class="col-sm">
              <label class="sr-only" for="inlineFormInputGroup">password</label>
              <div class="input-group mb-2">
                <div class="input-group-prepend">
                  <div class="input-group-text">*</div>
                </div>
